  const loadUsers = async () => {
    try {
      setIsLoading(true)
      
      // Usar la funci√≥n RPC personalizada
      const { data, error } = await supabase.rpc('get_users_for_admin')
      
      if (error) {
        console.error('Error loading users:', error)
        toast.error('Error al cargar usuarios: ' + error.message)
        return
      }

      // Adaptar los datos recibidos
      const adaptedUsers: UserWithRole[] = (data || []).map((user: any) => ({
        id: user.id,
        email: user.email,
        created_at: user.created_at,
        user_metadata: user.user_metadata || {},
        is_admin: user.is_admin
      }))

      setUsers(adaptedUsers)
      toast.success(`${adaptedUsers.length} usuarios cargados`)
    } catch (error) {
      console.error('Error loading users:', error)
      toast.error('Error al cargar usuarios')
    } finally {
      setIsLoading(false)
    }
  }

  const handleRoleUpdate = async (userId: string, isAdmin: boolean) => {
    try {
      // Usar la funci√≥n RPC para actualizar el rol
      const { data, error } = await supabase.rpc('update_user_role_safe', {
        target_user_id: userId,
        new_role: isAdmin ? 'admin' : 'user'
      })
      
      if (error) {
        throw error
      }
      
      // Recargar usuarios para reflejar cambios
      await loadUsers()
      toast.success(`Usuario ${isAdmin ? 'promovido a administrador' : 'degradado a usuario'}`)
    } catch (error: any) {
      console.error('Error updating user role:', error)
      toast.error('Error al actualizar el rol del usuario: ' + error.message)
    }
  }

  const filteredUsers = users.filter(user =>
    user.email.toLowerCase().includes(searchTerm.toLowerCase())
  )

  const isUserAdmin = (user: UserWithRole) => {
    return user.is_admin || 
           user.user_metadata?.role === 'admin' || 
           user.user_metadata?.isAdmin === true ||
           user.email === 'acevedojuanesteban.colombia@gmail.com'
  }

  if (isLoading) {
    return (
      <Card className="w-full">
        <CardContent className="p-6">
          <div className="text-center">
            <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-red-600 mx-auto mb-4"></div>
            <p>Cargando usuarios...</p>
          </div>
        </CardContent>
      </Card>
    )
  }

  return (
    <Card className="w-full">
      <CardHeader>
        <CardTitle className="flex items-center gap-2">
          <Users className="h-5 w-5" />
          Gesti√≥n de Usuarios
        </CardTitle>
      </CardHeader>
      <CardContent className="space-y-4">
        <div className="flex gap-4">
          <Input
            placeholder="Buscar por email..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            className="flex-1"
          />
          <Button onClick={loadUsers} variant="outline">
            Actualizar
          </Button>
        </div>

        <div className="space-y-2">
          {filteredUsers.map((user) => (
            <div
              key={user.id}
              className="flex items-center justify-between p-3 border rounded-lg"
            >
              <div className="flex items-center gap-3">
                <User className="h-5 w-5 text-gray-500" />
                <div>
                  <p className="font-medium">{user.email}</p>
                  <p className="text-sm text-gray-500">
                    Creado: {new Date(user.created_at).toLocaleDateString()}
                  </p>
                </div>
              </div>
              
              <div className="flex items-center gap-2">
                {isUserAdmin(user) ? (
                  <Badge variant="default" className="bg-purple-600">
                    <Crown className="h-3 w-3 mr-1" />
                    Admin
                  </Badge>
                ) : (
                  <Badge variant="secondary">
                    Usuario
                  </Badge>
                )}
                
                {user.id !== currentUser?.id && (
                  <Button
                    size="sm"
                    variant={isUserAdmin(user) ? "destructive" : "default"}
                    onClick={() => handleRoleUpdate(user.id, !isUserAdmin(user))}
                  >
                    {isUserAdmin(user) ? "Quitar Admin" : "Hacer Admin"}
                  </Button>
                )}
              </div>
            </div>
          ))}
        </div>

        {filteredUsers.length === 0 && (
          <div className="text-center py-8">
            <Users className="h-12 w-12 text-gray-400 mx-auto mb-4" />
            <p className="text-gray-500">
              {searchTerm ? 'No se encontraron usuarios' : 'No hay usuarios registrados'}
            </p>
          </div>
        )}

        {/* Informaci√≥n sobre la implementaci√≥n */}
        <div className="bg-green-50 p-4 rounded-lg">
          <p className="text-sm text-green-800">
            <strong>‚úÖ Implementaci√≥n completa:</strong> Esta gesti√≥n de usuarios usa funciones RPC 
            personalizadas en Supabase que verifican permisos de administrador de forma segura.
          </p>
        </div>
      </CardContent>
    </Card>
  )
}

components.config-checker.tsx:
"use client"

import { useEffect, useState } from "react";
import { supabase } from "@/lib/supabase";
import { Alert, AlertDescription, AlertTitle } from "./ui/alert";
import { CheckCircle, XCircle, AlertTriangle } from "lucide-react";

interface ConfigStatus {
  envVars: boolean;
  connection: boolean;
  realtime: boolean;
  tableAccess: boolean;
}

export function ConfigChecker() {
  const [configStatus, setConfigStatus] = useState<ConfigStatus | null>(null);
  const [isChecking, setIsChecking] = useState(true);

  useEffect(() => {
    const checkConfiguration = async () => {
      const status: ConfigStatus = {
        envVars: false,
        connection: false,
        realtime: false,
        tableAccess: false,
      };

      try {
        // Verificar variables de entorno
        const hasUrl = !!process.env.NEXT_PUBLIC_SUPABASE_URL;
        const hasKey = !!process.env.NEXT_PUBLIC_SUPABASE_ANON_KEY;
        status.envVars = hasUrl && hasKey;

        if (!status.envVars) {
          setConfigStatus(status);
          setIsChecking(false);
          return;
        }

        // Verificar conexi√≥n b√°sica
        try {
          const { data, error } = await supabase
            .from("wines")
            .select("id_vino")
            .limit(1);

          status.connection = !error;
          status.tableAccess = !error && data !== null;
        } catch (error) {
          console.error("Error checking connection:", error);
          status.connection = false;
          status.tableAccess = false;
        }

        // Verificar realtime (esto es m√°s complejo, solo verificamos que no haya errores inmediatos)
        try {
          const channel = supabase
            .channel("config-test")
            .on("postgres_changes", { event: "*", schema: "public", table: "wines" }, () => {})
            .subscribe((status) => {
              if (status === "SUBSCRIBED") {
                setConfigStatus(prev => prev ? { ...prev, realtime: true } : null);
                supabase.removeChannel(channel);
              } else if (status === "CHANNEL_ERROR") {
                setConfigStatus(prev => prev ? { ...prev, realtime: false } : null);
                supabase.removeChannel(channel);
              }
            });

          // Timeout para evitar esperar indefinidamente
          setTimeout(() => {
            if (configStatus?.realtime === undefined) {
              setConfigStatus(prev => prev ? { ...prev, realtime: false } : null);
              supabase.removeChannel(channel);
            }
          }, 5000);
        } catch (error) {
          console.error("Error checking realtime:", error);
          status.realtime = false;
        }

        setConfigStatus(status);
      } catch (error) {
        console.error("Error in configuration check:", error);
        setConfigStatus(status);
      } finally {
        setIsChecking(false);
      }
    };

    checkConfiguration();
  }, []);

  if (isChecking) {
    return (
      <Alert className="mb-4">
        <AlertTriangle className="h-4 w-4" />
        <AlertTitle>Verificando configuraci√≥n...</AlertTitle>
        <AlertDescription>
          Comprobando la configuraci√≥n de Supabase...
        </AlertDescription>
      </Alert>
    );
  }

  if (!configStatus) {
    return null;
  }

  const allGood = Object.values(configStatus).every(Boolean);
  const hasErrors = Object.values(configStatus).some(status => !status);

  if (allGood) {
    return (
      <Alert className="mb-4 border-green-200 bg-green-50">
        <CheckCircle className="h-4 w-4 text-green-600" />
        <AlertTitle className="text-green-800">Configuraci√≥n correcta</AlertTitle>
        <AlertDescription className="text-green-700">
          Todas las configuraciones de Supabase est√°n funcionando correctamente.
        </AlertDescription>
      </Alert>
    );
  }

  return (
    <Alert className="mb-4 border-red-200 bg-red-50">
      <XCircle className="h-4 w-4 text-red-600" />
      <AlertTitle className="text-red-800">Problemas de configuraci√≥n detectados</AlertTitle>
      <AlertDescription className="text-red-700 space-y-1">
        <div>Se encontraron los siguientes problemas:</div>
        <ul className="list-disc list-inside space-y-1 text-sm">
          {!configStatus.envVars && (
            <li>Variables de entorno de Supabase no configuradas</li>
          )}
          {!configStatus.connection && (
            <li>No se puede conectar a Supabase</li>
          )}
          {!configStatus.tableAccess && (
            <li>No se puede acceder a la tabla de vinos</li>
          )}
          {!configStatus.realtime && (
            <li>Realtime no est√° funcionando correctamente</li>
          )}
        </ul>
        <div className="text-xs mt-2">
          Consulta el archivo SUPABASE_SETUP.md para m√°s informaci√≥n sobre c√≥mo solucionar estos problemas.
        </div>
      </AlertDescription>
    </Alert>
  );
}

components.debug-realtime-status.tsx:
"use client"

import { useWineRealtimeContext } from "@/providers/wine-realtime-provider";
import { useWines } from "@/hooks/use-wines";
import { useState } from "react";
import { Button } from "./ui/button";
import { Badge } from "./ui/badge";
import { Card, CardContent, CardHeader, CardTitle } from "./ui/card";
import { RefreshCw, Wifi, WifiOff, AlertTriangle } from "lucide-react";

export function DebugRealtimeStatus() {
  const { wines: realtimeWines, isLoading, error, refetch } = useWineRealtimeContext();
  const { isUsingFallback, realtimeStatus } = useWines();
  const [isRefreshing, setIsRefreshing] = useState(false);

  const handleRefresh = async () => {
    setIsRefreshing(true);
    try {
      await refetch();
    } catch (error) {
      console.error('Error refreshing:', error);
    } finally {
      setIsRefreshing(false);
    }
  };

  const getStatusIcon = () => {
    switch (realtimeStatus) {
      case 'connected':
        return <Wifi className="h-4 w-4 text-green-500" />;
      case 'loading':
        return <RefreshCw className="h-4 w-4 text-yellow-500 animate-spin" />;
      case 'error':
        return <WifiOff className="h-4 w-4 text-red-500" />;
      default:
        return <AlertTriangle className="h-4 w-4 text-gray-500" />;
    }
  };

  const getStatusColor = () => {
    switch (realtimeStatus) {
      case 'connected':
        return 'bg-green-100 text-green-800 border-green-200';
      case 'loading':
        return 'bg-yellow-100 text-yellow-800 border-yellow-200';
      case 'error':
        return 'bg-red-100 text-red-800 border-red-200';
      default:
        return 'bg-gray-100 text-gray-800 border-gray-200';
    }
  };

  return (
    <Card className="w-full max-w-md">
      <CardHeader>
        <CardTitle className="flex items-center gap-2 text-sm">
          {getStatusIcon()}
          Estado de Conexi√≥n
        </CardTitle>
      </CardHeader>
      <CardContent className="space-y-3">
        <div className="flex items-center justify-between">
          <span className="text-sm text-gray-600">Estado:</span>
          <Badge className={getStatusColor()}>
            {realtimeStatus === 'connected' ? 'Conectado' : 
             realtimeStatus === 'loading' ? 'Conectando' : 'Error'}
          </Badge>
        </div>
        
        <div className="flex items-center justify-between">
          <span className="text-sm text-gray-600">Modo:</span>
          <Badge variant={isUsingFallback ? "secondary" : "default"}>
            {isUsingFallback ? 'Sin conexi√≥n' : 'Tiempo real'}
          </Badge>
        </div>
        
        <div className="flex items-center justify-between">
          <span className="text-sm text-gray-600">Vinos cargados:</span>
          <span className="text-sm font-medium">{realtimeWines.length}</span>
        </div>
        
        <div className="flex items-center justify-between">
          <span className="text-sm text-gray-600">Cargando:</span>
          <span className="text-sm font-medium">{isLoading ? 'S√≠' : 'No'}</span>
        </div>
        
        {error && (
          <div className="bg-red-50 border border-red-200 rounded p-2">
            <p className="text-xs text-red-800">
              <strong>Error:</strong> {error.message}
            </p>
          </div>
        )}
        
        <Button 
          onClick={handleRefresh} 
          disabled={isRefreshing}
          size="sm"
          className="w-full"
        >
          <RefreshCw className={`h-4 w-4 mr-2 ${isRefreshing ? 'animate-spin' : ''}`} />
          {isRefreshing ? 'Actualizando...' : 'Actualizar'}
        </Button>
      </CardContent>
    </Card>
  );
}

components.filters-sidebar.tsx:
"use client"

import { useState } from "react"
import { Filter, X } from "lucide-react"
import { Button } from "@/components/ui/button"
import { Checkbox } from "@/components/ui/checkbox"
import { Label } from "@/components/ui/label"
import { Accordion, AccordionContent, AccordionItem, AccordionTrigger } from "@/components/ui/accordion"
import { getCountryFlag } from "@/utils/price"
import { PriceRangeSlider } from "./price-range-slider"

interface FiltersSidebarProps {
  variedades: string[]
  bodegas: string[]
  paises: string[]
  colores: string[]
  selectedVariedades: string[]
  selectedBodegas: string[]
  selectedPaises: string[]
  selectedColores: string[]
  priceRange: { min: number; max: number }
  currentPriceRange: { min: number; max: number }
  onToggleVariedad: (variedad: string) => void
  onToggleBodega: (bodega: string) => void
  onTogglePais: (pais: string) => void
  onToggleColor: (color: string) => void
  onUpdatePriceRange: (min: number, max: number) => void
  onClearFilters: () => void
  hasActiveFilters: boolean
  varietalCounts: Record<string, number>
  bodegaCounts: Record<string, number>
  paisCounts: Record<string, number>
  colorCounts: Record<string, number>
  isOpen?: boolean
  setIsOpen?: (open: boolean) => void
}

// Componente reutilizable para elementos de filtro
interface FilterItemProps {
  id: string
  label: string
  checked: boolean
  count: number
  onToggle: () => void
  icon?: React.ReactNode
  hasScroll?: boolean
}

function FilterItem({ id, label, checked, count, onToggle, icon, hasScroll = false }: FilterItemProps) {
  return (
    <div className="flex items-center justify-between">
      <div className="flex items-center space-x-2">
        <Checkbox
          id={id}
          checked={checked}
          onCheckedChange={onToggle}
        />
        <Label htmlFor={id} className="text-sm font-normal flex items-center space-x-2">
          {icon && <span>{icon}</span>}
          <span>{label}</span>
        </Label>
      </div>
      <span className="text-xs text-gray-500 bg-gray-100 px-2 py-1 rounded-full">
        {count}
      </span>
    </div>
  )
}

// Componente reutilizable para secciones de filtro
interface FilterSectionProps {
  value: string
  title: string
  items: Array<{
    id: string
    label: string
    checked: boolean
    count: number
    onToggle: () => void
    icon?: React.ReactNode
  }>
  hasScroll?: boolean
}

function FilterSection({ value, title, items, hasScroll = false }: FilterSectionProps) {
  return (
    <AccordionItem value={value}>
      <AccordionTrigger className="text-base font-medium">{title}</AccordionTrigger>
      <AccordionContent>
        <div className={`space-y-3 ${hasScroll ? 'max-h-52 overflow-y-auto' : ''}`}>
          {items.map((item, index) => (
            <FilterItem
              key={`${item.id}-${index}`}
              id={item.id}
              label={item.label}
              checked={item.checked}
              count={item.count}
              onToggle={item.onToggle}
              icon={item.icon}
            />
          ))}
        </div>
      </AccordionContent>
    </AccordionItem>
  )
}

// Funci√≥n helper para obtener el icono del color
function getColorIcon(color: string) {
  return (
    <div
      className={`w-3 h-3 rounded-full ${
        color === "Tinto"
          ? "bg-red-600"
          : color === "Blanco"
            ? "bg-yellow-200 border border-yellow-400"
            : "bg-pink-300"
      }`}
    />
  )
}

export function FiltersSidebar({
  variedades,
  bodegas,
  paises,
  colores,
  selectedVariedades,
  selectedBodegas,
  selectedPaises,
  selectedColores,
  priceRange,
  currentPriceRange,
  onToggleVariedad,
  onToggleBodega,
  onTogglePais,
  onToggleColor,
  onUpdatePriceRange,
  onClearFilters,
  hasActiveFilters,
  varietalCounts,
  bodegaCounts,
  paisCounts,
  colorCounts,
  isOpen: externalIsOpen,
  setIsOpen: externalSetIsOpen,
}: FiltersSidebarProps) {
  const [internalIsOpen, setInternalIsOpen] = useState(false)

  const isOpen = externalIsOpen !== undefined ? externalIsOpen : internalIsOpen
  const setIsOpen = externalSetIsOpen || setInternalIsOpen

  // Configuraci√≥n de las secciones de filtro
  const filterSections = [
    {
      value: "variedades",
      title: "Variedades",
      items: variedades.map((variedad) => {
        const key = variedad.trim().toLowerCase();
        return {
          id: variedad,
          label: variedad,
          checked: selectedVariedades.includes(variedad),
          count: varietalCounts[key] || 0,
          onToggle: () => onToggleVariedad(variedad),
        }
      }),
      hasScroll: true,
    },
    {
      value: "paises",
      title: "Pa√≠ses",
      items: paises.map((pais) => ({
        id: pais,
        label: pais,
        checked: selectedPaises.includes(pais),
        count: paisCounts[pais] || 0,
        onToggle: () => onTogglePais(pais),
        icon: getCountryFlag(pais),
      })),
    },
    {
      value: "bodegas",
      title: "Bodegas",
      items: bodegas.map((bodega) => ({
        id: bodega,
        label: bodega,
        checked: selectedBodegas.includes(bodega),
        count: bodegaCounts[bodega] || 0,
        onToggle: () => onToggleBodega(bodega),
      })),
      hasScroll: true,
    },
    {
      value: "colores",
      title: "Tipo de Vino",
      items: colores.map((color) => ({
        id: color,
        label: color,
        checked: selectedColores.includes(color),
        count: colorCounts[color] || 0,
        onToggle: () => onToggleColor(color),
        icon: getColorIcon(color),
      })),
    },
  ]

  return (
    <>
      {/* Overlay para m√≥vil/tablet */}
      {isOpen && (
        <div className="lg:hidden fixed inset-0 bg-black bg-opacity-50 z-40" onClick={() => setIsOpen(false)} />
      )}

      {/* Sidebar */}
      <aside
        className={`bg-gradient-to-b from-white to-red-50 rounded-lg shadow-lg border border-red-100 p-6 h-fit lg:block lg:static lg:translate-x-0 w-72 ${isOpen ? "block" : "hidden"}
        lg:z-auto z-50
        fixed lg:relative top-0 left-0 lg:top-auto lg:left-auto
        max-h-screen lg:max-h-none overflow-y-auto
      `}
      >
        {/* Bot√≥n cerrar para m√≥vil/tablet */}
        <div className="lg:hidden flex justify-between items-center mb-4">
          <h2 className="text-lg font-semibold">Filtros</h2>
          <Button variant="ghost" size="sm" onClick={() => setIsOpen(false)}>
            <X className="h-4 w-4" />
          </Button>
        </div>

        {/* T√≠tulo del sidebar */}
        <div className="hidden lg:flex items-center space-x-2 mb-6">
          <Filter className="h-5 w-5 text-red-600" />
          <h2 className="text-lg font-semibold text-red-900">Filtros</h2>
        </div>

        {/* Filtro de precio */}
        <div className="mb-6">
          <PriceRangeSlider
            minPrice={priceRange.min}
            maxPrice={priceRange.max}
            currentMin={currentPriceRange.min}
            currentMax={currentPriceRange.max}
            onRangeChange={onUpdatePriceRange}
          />
        </div>

        {/* Acorde√≥n con filtros */}
        <Accordion type="multiple" defaultValue={["variedades", "bodegas", "paises", "colores"]}>
          {filterSections.map((section, index) => (
            <FilterSection
              key={`${section.value}-${index}`}
              value={section.value}
              title={section.title}
              items={section.items}
              hasScroll={section.hasScroll}
            />
          ))}
        </Accordion>

        {/* Botones de acci√≥n */}
        {hasActiveFilters && (
          <>
            <Button variant="outline" onClick={onClearFilters} className="w-full mt-4">
              Limpiar Filtros
            </Button>
            <Button onClick={() => setIsOpen(false)} className="lg:hidden w-full mt-2 bg-red-600 hover:bg-red-700">
              Aplicar Filtros
            </Button>
          </>
        )}
      </aside>
    </>
  )
}

components.LoginForm.tsx:
import { useState } from "react";
import { useAuth } from "@/providers/auth-provider";

export default function LoginForm() {
  const { login } = useAuth();
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState<string | null>(null);
  const [success, setSuccess] = useState(false);

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setError(null);
    setSuccess(false);
    try {
      await login(email);
      setSuccess(true);
    } catch (err: any) {
      setError(err.message);
    }
  };

  return (
    <form onSubmit={handleSubmit} className="flex flex-col gap-4 max-w-sm mx-auto mt-8 p-6 bg-white rounded shadow">
      <h2 className="text-xl font-bold mb-2">Iniciar sesi√≥n</h2>
      <input
        value={email}
        onChange={e => setEmail(e.target.value)}
        placeholder="Email"
        type="email"
        className="border p-2 rounded"
        required
      />
      <input
        value={password}
        onChange={e => setPassword(e.target.value)}
        type="password"
        placeholder="Contrase√±a"
        className="border p-2 rounded"
        required
      />
      <button type="submit" className="bg-red-600 text-white py-2 rounded hover:bg-red-700 transition">Iniciar sesi√≥n</button>
      {error && <div className="text-red-600 text-sm">{error}</div>}
      {success && <div className="text-green-600 text-sm">¬°Sesi√≥n iniciada correctamente!</div>}
    </form>
  );
}

