components.wine-store.tsx:
"use client"

import { useState, useEffect } from "react"
import { Header } from "./header"
import { StoreView } from "./views/store-view"
import { AdminView } from "./views/admin-view"
import { CartView } from "./views/cart-view"
import { Footer } from "./footer"
import { useAuth } from "@/providers/auth-provider"
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs"
import { setViewInURL, getViewFromURL } from "@/lib/utils"
import { useConsumibles } from '../hooks/use-consumibles';
import { Card, CardHeader, CardTitle, CardDescription, CardContent, CardFooter } from '../components/ui/card';
import { Button } from '../components/ui/button';
import type { Wine } from "@/types"

type ViewType = "store" | "admin" | "cart"

type WineStoreProps = {
  wines: Wine[]
}

export default function WineStore({ wines }: WineStoreProps) {
  const { user } = useAuth()

  const [currentView, setCurrentView] = useState<ViewType>("store")
  const [searchTerm, setSearchTerm] = useState("")
  const [cartItemCount, setCartItemCount] = useState(0)
  const [filteredWinesCount, setFilteredWinesCount] = useState(0)
  const [isSidebarOpen, setIsSidebarOpen] = useState(false)

  const { consumibles, loading: loadingConsumibles, error: errorConsumibles } = useConsumibles();

  // Si no hay par√°metro 'view', establecerlo en 'store' y actualizar la URL
  useEffect(() => {
    const viewFromUrl = getViewFromURL() as ViewType | null;
    if (!viewFromUrl) {
      setCurrentView("store");
      setViewInURL("store");
    }
  }, []);

  // Sincronizar la vista con la URL solo en el cliente
  useEffect(() => {
    const viewFromUrl = getViewFromURL() as ViewType
    if (viewFromUrl && viewFromUrl !== currentView) {
      if (viewFromUrl === "admin" && !user?.isAdmin) {
        setCurrentView("store")
        setViewInURL("store")
      } else {
        setCurrentView(viewFromUrl)
      }
    }
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [user?.isAdmin])

  // Guardar la vista en la URL cuando cambie
  useEffect(() => {
    setViewInURL(currentView)
  }, [currentView])

  // Determinar qu√© vistas est√°n disponibles seg√∫n el tipo de usuario
  const availableViews = user?.isAdmin
    ? ["store", "admin", "cart"] as ViewType[]
    : ["store", "cart"] as ViewType[]

  const handleViewChange = (view: ViewType) => {
    setCurrentView(view)
  }

  return (
    <div className="min-h-screen w-full bg-white relative flex flex-col">
      <Header
        currentView={currentView}
        onViewChange={handleViewChange}
        searchTerm={searchTerm}
        onSearchChange={setSearchTerm}
        cartItemCount={cartItemCount}
        onCartClick={() => { }} // Se maneja dentro de StoreView
        onFiltersClick={() => setIsSidebarOpen(true)}
        filteredWinesCount={filteredWinesCount} // Se calcula dentro de StoreView
        availableViews={availableViews}
      />

      <main className="flex-1">
        <div className="container mx-auto px-4 py-6">
          {currentView === "store" && (
            <Tabs defaultValue="Vinos"  className="w-full">
              <TabsList>
                <TabsTrigger value="Vinos">Vinos</TabsTrigger>
                <TabsTrigger value="Comestibles">Comestibles</TabsTrigger>
              </TabsList>
              <TabsContent value="Vinos">
                <StoreView
                  wines={wines}
                  searchTerm={searchTerm}
                  onSearchChange={setSearchTerm}
                  onFiltersClick={() => setIsSidebarOpen(true)}
                  onCartItemCountChange={setCartItemCount}
                  onFilteredWinesCountChange={setFilteredWinesCount}
                  isSidebarOpen={isSidebarOpen}
                  onSidebarToggle={() => setIsSidebarOpen(!isSidebarOpen)}
                />
              </TabsContent>
              <TabsContent value="Comestibles">
                {loadingConsumibles ? (
                  <div className="flex items-center justify-center h-32" role="status" aria-live="polite">
                    <div className="text-center">
                      <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-yellow-600 mx-auto mb-4" aria-hidden="true"></div>
                      <p className="text-gray-600">Cargando consumibles...</p>
                    </div>
                  </div>
                ) : errorConsumibles ? (
                  <div className="text-center text-red-500">{errorConsumibles}</div>
                ) : consumibles.length === 0 ? (
                  <div className="text-center text-gray-500">No hay consumibles disponibles.</div>
                ) : (
                  <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
                    {consumibles.map((item) => (
                      <Card key={item.id} className="flex flex-col items-center">
                        <CardHeader className="flex flex-col items-center">
                          <img src={item.url_imagen} alt={item.nombre} className="w-32 h-32 object-cover mb-2 rounded" />
                          <CardTitle className="text-lg text-center">{item.nombre}</CardTitle>
                          <CardDescription className="text-center">{item.descripcion}</CardDescription>
                        </CardHeader>
                        <CardContent className="flex flex-col items-center">
                          <span className="font-semibold text-primary mb-2">${item.precio}</span>
                        </CardContent>
                        <CardFooter className="w-full flex justify-center">
                          <Button className="w-full">Agregar al carrito</Button>
                        </CardFooter>
                      </Card>
                    ))}
                  </div>
                )}
              </TabsContent>
            </Tabs>
          )}

          {currentView === "admin" && <AdminView wines={wines} />}

          {currentView === "cart" && (
            <CartView onBack={() => handleViewChange("store")} />
          )}
        </div>
      </main>

      <Footer />
    </div>
  )
}

components.wine-table.tsx:
"use client"

import type React from "react"
import { useRef, useState, useEffect } from "react"
import * as XLSX from "xlsx"
import { Download, Upload, Wine as WineIcon, SquarePen, Trash2, Plus, Palette, Grape, MapPin, Icon, DollarSign, StickyNote, Clock, Percent, Search, ChevronLeft, ChevronRight } from "lucide-react"
import { wineGlassBottle } from '@lucide/lab'
import { Button } from "./ui/button"
import { Wine } from "@/types"
import { toast } from "sonner"
import { WineService } from "@/lib/services/wine-service"
import EditWineModal from "./modales/edit-wine-modal"
import { Input } from "./ui/input"
import DeleteWineModal from "./modales/delete-wine-modal"

interface WineTableProps {
  wines?: Wine[]
  onWinesChange?: (wines: Wine[]) => void
  className?: string
}

interface SortConfig {
  key: string
  direction: 'asc' | 'desc'
}

interface SortConfig {
  key: string
  direction: 'asc' | 'desc'
}

export function WineTable({ wines = [], onWinesChange, className }: WineTableProps) {
  const [internalWines, setInternalWines] = useState<Wine[]>([])
  const [sortConfig, setSortConfig] = useState<SortConfig>({
    key: 'nombre',
    direction: 'asc'
  })
  const [currentPage, setCurrentPage] = useState(1)
  const [itemsPerPage] = useState(10)

  const [searchTerm, setSearchTerm] = useState("")
  const inputRef = useRef<HTMLInputElement>(null)

  const winesToDisplay = wines

  // Filtrar vinos por t√©rmino de b√∫squeda
  const filteredWines = winesToDisplay.filter((wine) =>
    wine.nombre.toLowerCase().includes(searchTerm.toLowerCase())
  )

  // Calcular paginaci√≥n
  const totalPages = Math.ceil(filteredWines.length / itemsPerPage)
  const startIndex = (currentPage - 1) * itemsPerPage
  const endIndex = startIndex + itemsPerPage
  const currentWines = filteredWines.slice(startIndex, endIndex)

  // Resetear a la primera p√°gina cuando cambie el t√©rmino de b√∫squeda
  useEffect(() => {
    setCurrentPage(1)
  }, [searchTerm])

  const setWines = (newWines: any[]) => {
    if (onWinesChange) {
      onWinesChange(newWines)
    } else {
      // Esto ya no es necesario si siempre usamos realtimeWines
    }
  }

  const exportToExcel = () => {
    try {
      const flatData = winesToDisplay.map((w) => ({
        "id vino": w.id_vino,
        stock: w.stock,
        nombre: w.nombre,
        precio: w.precio,
        // "url imagen": w.url_imagen,
        descripcion: w.descripcion,
        "nivel alcohol": w.nivel_alcohol,
        variedades: w.variedades.join(", "),
        "pais importacion": w.pais_importacion,
        "color vino": w.color_vino,
        bodega: w.wine_details.bodega,
        "id detalle": w.wine_details.id_detalle,
        "notas cata": w.wine_details.notas_cata,
        "tipo crianza": w.wine_details.tipo_crianza,
        "contenido azucar": w.wine_details.contenido_azucar,
        "contenido carbonico": w.wine_details.contenido_carbonico,
      }))

      const ws = XLSX.utils.json_to_sheet(flatData)

      // Obtener el rango de datos
      const range = XLSX.utils.decode_range(ws["!ref"] || "A1")

      // Configurar protecci√≥n para todas las celdas (inicialmente desbloqueadas)
      for (let row = range.s.r; row <= range.e.r; row++) {
        for (let col = range.s.c; col <= range.e.c; col++) {
          const cellAddress = XLSX.utils.encode_cell({ r: row, c: col })
          if (!ws[cellAddress]) continue

          // Desbloquear todas las celdas por defecto
          if (!ws[cellAddress].s) ws[cellAddress].s = {}
          ws[cellAddress].s.protection = { locked: false }
        }
      }

      // Bloquear espec√≠ficamente las columnas "id vino" (columna A) e "id detalle" (columna L)
      const protectedColumns = [0, 11] // Columna A (id vino) y columna L (id detalle)

      for (let row = range.s.r; row <= range.e.r; row++) {
        protectedColumns.forEach((col) => {
          const cellAddress = XLSX.utils.encode_cell({ r: row, c: col })
          if (ws[cellAddress]) {
            if (!ws[cellAddress].s) ws[cellAddress].s = {}
            ws[cellAddress].s.protection = { locked: true }

            // Agregar color de fondo para indicar que est√° bloqueada
            ws[cellAddress].s.fill = {
              fgColor: { rgb: "FFE6E6" }, // Color rojo claro
              bgColor: { rgb: "FFE6E6" },
            }
          }
        })
      }

      // Configurar protecci√≥n del worksheet
      ws["!protect"] = {
        password: "admin123",
        selectLockedCells: true,
        selectUnlockedCells: true,
        formatCells: false,
        formatColumns: false,
        formatRows: false,
        insertColumns: false,
        insertRows: false,
        insertHyperlinks: false,
        deleteColumns: false,
        deleteRows: false,
        sort: false,
        autoFilter: false,
        pivotTables: false,
      }

      const wb = XLSX.utils.book_new()
      XLSX.utils.book_append_sheet(wb, ws, "Vinos")
      XLSX.writeFile(wb, "inventario_vinos.xlsx")

      toast.success(
        "Inventario exportado exitosamente", {
        description: "Las columnas ID est√°n protegidas contra edici√≥n",
        action: {
          label: "cerrar",
          onClick: () => "",
        },
      })
    } catch (error) {
      toast.error("Error al exportar el inventario", {
        description: "Por favor, intenta nuevamente m√°s tarde.",
        action: {
          label: "cerrar",
          onClick: () => "",
        },
      })
    }
  }

  const handleImport = async (e: React.ChangeEvent<HTMLInputElement>) => {
    const file = e.target.files?.[0]
    if (!file) return

    try {
      const data = await file.arrayBuffer()
      const workbook = XLSX.read(data, { type: "array" })
      const sheet = workbook.Sheets[workbook.SheetNames[0]]
      const json = XLSX.utils.sheet_to_json<any>(sheet)

      const imported: any[] = json.map((row: any) => ({
        id_vino: row["id vino"] || row.id_vino || `${Date.now()}-${Math.random().toString(36).substr(2, 9)}`,
        stock: Number(row.stock) || 0,
        nombre: row.nombre,
        precio: Number(row.precio),
        // url_imagen: row["url imagen"] || row.url_imagen || "",
        descripcion: row.descripcion,
        nivel_alcohol: Number(row["nivel alcohol"] || row.nivel_alcohol),
        variedades: row.variedades?.split(",").map((v: string) => v.trim()) ?? [],
        pais_importacion: row["pais importacion"] || row.pais_importacion,
        color_vino: row["color vino"] || row.color_vino,
        anada: Number(row.anada),
        wine_details: {
          bodega: row.bodega,
          id_vino: row["id vino"] || row.id_vino || `${Date.now()}-${Math.random().toString(36).substr(2, 9)}`,
          id_detalle: row["id detalle"] || row.id_detalle || `${Date.now()}-${Math.random().toString(36).substr(2, 9)}`,
          notas_cata: row["notas cata"] || row.notas_cata,
          tipo_crianza: row["tipo crianza"] || row.tipo_crianza,
          contenido_azucar: row["contenido azucar"] || row.contenido_azucar,
          contenido_carbonico: row["contenido carbonico"] || row.contenido_carbonico,
        },
      }))

      const errores = imported.filter(
        (w) =>
          !w.nombre ||
          typeof w.stock !== "number" ||
          typeof w.precio !== "number" ||
          typeof w.nivel_alcohol !== "number" ||
          isNaN(w.stock) ||
          isNaN(w.precio) ||
          isNaN(w.nivel_alcohol),
      )

      if (errores.length > 0) {
        // showNotification("error", "Error en el archivo: verifica que los campos num√©ricos sean correctos")
        toast.error("Error al importar el inventario", {
          description: "Error en el archivo: verifica que los campos num√©ricos sean correctos.",
          action: {
            label: "cerrar",
            onClick: () => ""
          },
        })
        return
      }

      setWines(imported)
      // showNotification("success", `${imported.length} vinos importados exitosamente`)
      toast.success("Inventario", {
        description: `${imported.length} vinos importados exitosamente`,
        action: {
          label: "cerrar",
          onClick: () => ""
        },
      })

      // Reset file input
      if (inputRef.current) {
        inputRef.current.value = ""
      }
    } catch (error) {
      // showNotification("error", "Error al procesar el archivo Excel")
      toast.error("Error al exportar el inventario", {
        description: "Error al procesar el archivo Excel.",
        action: {
          label: "cerrar",
          onClick: () => ""
        },
      })
    }
  }

  const formatPrice = (price: number) => {
    return new Intl.NumberFormat("es-CO", {
      style: "currency",
      currency: "COP",
      minimumFractionDigits: 0,
    }).format(price)
  }

  // Funci√≥n para generar n√∫meros de p√°gina
  const getPageNumbers = () => {
    const pages = []
    const maxVisiblePages = 5

    if (totalPages <= maxVisiblePages) {
      for (let i = 1; i <= totalPages; i++) {
        pages.push(i)
      }
    } else {
      if (currentPage <= 3) {
        for (let i = 1; i <= 4; i++) {
          pages.push(i)
        }
        pages.push('ellipsis')
        pages.push(totalPages)
      } else if (currentPage >= totalPages - 2) {
        pages.push(1)
        pages.push('ellipsis')
        for (let i = totalPages - 3; i <= totalPages; i++) {
          pages.push(i)
        }
      } else {
        pages.push(1)
        pages.push('ellipsis')
        for (let i = currentPage - 1; i <= currentPage + 1; i++) {
          pages.push(i)
        }
        pages.push('ellipsis')
        pages.push(totalPages)
      }
    }

    return pages
  }

  const getColorBadge = (color: string) => {
    const colorClasses = {
      Tinto: "bg-red-100 text-red-800 border-red-200",
      Blanco: "bg-amber-100 text-amber-800 border-amber-200",
      Ros√©: "bg-pink-100 text-pink-800 border-pink-200",
      Espumoso: "bg-blue-100 text-blue-800 border-blue-200",
    }

    return colorClasses[color as keyof typeof colorClasses] || "bg-gray-100 text-gray-800 border-gray-200"
  }

  useEffect(() => {
    if (typeof window !== "undefined" && totalPages > 0) {
      const url = new URL(window.location.href);
      const pageParam = url.searchParams.get("page");
      const page = pageParam ? parseInt(pageParam) : 1;
      if (!isNaN(page) && page > 0 && page <= totalPages) {
        setCurrentPage(page);
      }
    }
  }, [totalPages]);

  return (
    <div className={`relative ${className}`}>
      {/* Header with controls */}
      <div className="flex items-center justify-between flex-wrap gap-4 p-6 pt-0">
        <div className="flex items-center gap-4">
          <div className="bg-red-100 p-3 rounded-xl">
            <WineIcon className="h-6 w-6 text-red-600" />
          </div>
          <div>
            <h2 className="text-2xl font-bold text-red-900">Administraci√≥n de Vinos</h2>
            <div className="flex items-center gap-2 mt-1">
              <div className="w-2 h-2 rounded-full bg-green-500"></div>
              <span className="text-xs text-gray-600">
                Datos cargados desde el servidor
              </span>
            </div>
          </div>
        </div>

        <div className="flex gap-3 flex-wrap">
          <Button
            className="flex items-center gap-2 bg-green-600 hover:bg-green-700 text-white px-4 py-2 rounded-xl font-medium transition-all duration-200 shadow-lg hover:shadow-xl transform hover:-translate-y-0.5 text-sm"
          >
            <Plus className="h-4 w-4" />
            Agregar Vino
          </Button>
          <Button
            onClick={exportToExcel}
            className="flex items-center gap-2 bg-red-600 hover:bg-red-700 text-white px-4 py-2 rounded-xl font-medium transition-all duration-200 shadow-lg hover:shadow-xl transform hover:-translate-y-0.5 text-sm"
          >
            <Download className="h-4 w-4" />
            Exportar Excel
          </Button>

          <label className="flex items-center gap-2 bg-amber-500 hover:bg-amber-600 text-white px-4 py-2 rounded-xl font-medium transition-all duration-200 shadow-lg hover:shadow-xl transform hover:-translate-y-0.5 cursor-pointer text-sm">
            <Upload className="h-4 w-4" />
            Importar Excel
            <Input ref={inputRef} type="file" accept=".xlsx,.xls" onChange={handleImport} className="hidden" />
          </label>
        </div>
      </div>

      {/* Search Bar */}
      <div className="px-6 pb-4 flex justify-center">
        <div className="relative max-w-xl w-full">
          <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
            <Search className="h-5 w-5 text-gray-400" />
          </div>
          <Input
            type="text"
            placeholder="Buscar vino por nombre..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            className="pl-10 pr-4 py-2 border border-gray-300 rounded-xl focus:ring-2 focus:ring-red-500 focus:border-red-500 transition-all duration-200 focus-visible:ring-0 focus-visible:ring-offset-0"
          />
          {searchTerm && (
            <button
              onClick={() => setSearchTerm("")}
              className="absolute inset-y-0 right-0 pr-3 flex items-center text-gray-400 hover:text-gray-600"
            >
              <span className="text-lg">√ó</span>
            </button>
          )}
        </div>
        {searchTerm && (
          <p className="text-sm text-gray-600 mt-2">
            Mostrando {filteredWines.length} de {winesToDisplay.length} vinos
          </p>
        )}
      </div>

      {/* Table */}
      <div className="bg-white rounded-2xl shadow-xl overflow-hidden border border-red-100">
        <div className="overflow-x-auto">
          <table className="w-full">
            <thead className="bg-gradient-to-r from-red-600 to-red-700 text-white">
              <tr>
                {/* Sticky First Column */}
                <th className="sticky left-0 z-30 bg-gradient-to-r from-red-600 to-red-700 px-6 py-4 text-left text-sm font-semibold uppercase tracking-wider border-r border-red-500 w-80 min-w-72 max-w-96 shadow-lg">
                  <div className="flex items-center gap-2">
                    <WineIcon size={16} />
                    Vino
                  </div>
                </th>

                {/* Scrollable Middle Columns */}
                <th className="px-6 py-4 text-center text-sm font-semibold uppercase tracking-wider whitespace-nowrap">
                  <div className="flex items-center gap-2">
                    <Icon size={16} iconNode={wineGlassBottle} />
                    Stock
                  </div>
                </th>
                <th className="px-6 py-4 text-center text-sm font-semibold uppercase tracking-wider whitespace-nowrap">
                  <div className="flex items-center gap-2">
                    <DollarSign size={16} />
                    Precio
                  </div>
                </th>
                <th className="px-6 py-4 text-center text-sm font-semibold uppercase tracking-wider whitespace-nowrap">
                  <div className="flex items-center gap-2">
                    <Clock size={16} />
                    Tiempo
                  </div>
                </th>
                <th className="px-6 py-4 text-center text-sm font-semibold uppercase tracking-wider whitespace-nowrap">
                  <div className="flex items-center justify-center gap-2">
                    <Percent size={16} />
                    Alcohol
                  </div>
                </th>
                <th className="px-6 py-4 text-center text-sm font-semibold uppercase tracking-wider whitespace-nowrap">
                  <div className="flex items-center justify-center gap-2">
                    <Palette size={16} />
                    Color
                  </div>
                </th>
                <th className="px-6 py-4 text-center text-sm font-semibold uppercase tracking-wider whitespace-nowrap">
                  <div className="flex items-center justify-center gap-2">
                    <Grape size={16} />
                    Variedades
                  </div>
                </th>
                <th className="px-6 py-4 text-center text-sm font-semibold uppercase tracking-wider whitespace-nowrap w-56 min-w-56">
                  <div className="flex items-center justify-center gap-2">
                    <MapPin size={16} />
                    Bodega
                  </div>
                </th>
                <th className="px-6 py-4 text-center text-sm font-semibold uppercase tracking-wider whitespace-nowrap w-auto min-w-56">
                  <div className="flex items-center justify-center gap-2">
                    <StickyNote size={16} />
                    Notas de Cata
                  </div>
                </th>
                <th className="px-6 py-4 text-center text-sm font-semibold uppercase tracking-wider whitespace-nowrap">
                  <div className="flex items-center justify-center gap-2">
                    <StickyNote size={16} />
                    Capacidad
                  </div>
                </th>

                {/* Sticky Last Column */}
                <th className="sticky right-0 z-30 bg-gradient-to-r from-red-600 to-red-700 px-6 py-4 text-center text-sm font-semibold uppercase tracking-wider border-l border-red-500 w-32 min-w-32 max-w-32 shadow-lg">
                  Acciones
                </th>
              </tr>
            </thead>

            <tbody className="divide-y divide-gray-100">
              {currentWines.map((wine: Wine, index) => (
                <tr key={wine.id_vino} className="hover:bg-red-50 bg-white">
                  {/* Sticky First Column */}
                  <td className={`sticky left-0 z-20 px-6 py-4 border-r border-gray-200 w-72 min-w-72 max-w-72 bg-white shadow-lg`}>
                    <div className="flex items-center gap-4">
                      {wine.url_imagen && (
