                  </td>

                  {/* Scrollable Middle Columns */}
                  <td className="px-6 py-4 text-center">
                    <span className={`inline-flex px-3 py-1 rounded-full text-sm font-semibold ${wine.stock > 20 ? 'bg-green-100 text-green-800' :
                      wine.stock > 10 ? 'bg-yellow-100 text-yellow-800' :
                        'bg-red-100 text-red-800'
                      }`}>
                      {wine.stock} unidades
                    </span>
                  </td>
                  <td className="px-6 py-4 text-start">
                    <span className="font-semibold text-gray-900 text-lg">
                      {formatPrice(wine.precio)}
                    </span>
                  </td>
                  <td className="px-6 py-4 text-center">
                    <span className="font-semibold text-gray-900 text-lg">
                      {wine.wine_details.tipo_crianza}
                    </span>
                  </td>
                  <td className="px-6 py-4 text-center">
                    <span className="bg-blue-100 text-blue-800 px-3 py-1 rounded-full text-sm font-medium">
                      {wine.nivel_alcohol}%
                    </span>
                  </td>
                  <td className="px-6 py-4 text-center">
                    <span className={`inline-flex px-3 py-1 rounded-full text-sm font-medium border ${getColorBadge(wine.color_vino)}`}>
                      {wine.color_vino}
                    </span>
                  </td>
                  <td className="px-6 py-4">
                    <div className="flex flex-wrap gap-1 justify-center">
                      {wine.variedades.map((variedad: string, idx: number) => (
                        <span
                          key={idx}
                          className="bg-purple-100 text-purple-800 px-2 py-1 rounded-md text-xs font-medium whitespace-nowrap"
                        >
                          {variedad}
                        </span>
                      ))}
                    </div>
                  </td>
                  <td className="px-6 py-4 text-center">
                    {wine.wine_details.bodega}
                  </td>
                  <td className="px-6 py-4">
                    <p className="text-gray-600 text-sm leading-relaxed">
                      {wine.wine_details.notas_cata}
                    </p>
                  </td>
                  <td className="px-6 py-4 text-center">
                    <span className="font-semibold text-gray-900 text-lg">
                      {wine.capacidad}
                    </span>
                  </td>

                  {/* Sticky Last Column */}
                  <td className={`sticky right-0 z-20 px-6 py-4 border-l border-gray-200 w-32 min-w-32 max-w-32 shadow-lg ${index % 2 === 0 ? 'bg-white' : 'bg-gray-50'}`}>
                    <div className="flex items-center justify-center gap-2 w-full">
                      <EditWineModal vino={wine}>
                        <div className="bg-blue-600 hover:bg-blue-700 text-white p-2 h-8 w-8 rounded-md flex items-center justify-center cursor-pointer transition-colors">
                          <SquarePen size={14} />
                        </div>
                      </EditWineModal>
                      <DeleteWineModal vino={wine}>
                        <div className="bg-red-600 hover:bg-red-700 text-white p-2 h-8 w-8 rounded-md flex items-center justify-center cursor-pointer transition-colors">
                          <Trash2 size={14} />
                        </div>
                      </DeleteWineModal>
                    </div>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>

      {/* Paginaci√≥n */}
      {filteredWines.length > 0 && totalPages > 1 && (
        <div className="flex items-center justify-between px-6 py-4 bg-white border-t border-gray-200 mt-2 rounded-2xl">
          <div className="text-sm text-gray-700">
            Mostrando {startIndex + 1} a {Math.min(endIndex, filteredWines.length)} de {filteredWines.length} vinos
          </div>

          <div className="flex items-center gap-2">
            <Button
              variant="outline"
              size="sm"
              onClick={() => setCurrentPage(Math.max(1, currentPage - 1))}
              disabled={currentPage === 1}
              className="flex items-center gap-1"
            >
              <ChevronLeft className="h-4 w-4" />
              Anterior
            </Button>

            <div className="flex items-center gap-1">
              {getPageNumbers().map((page, index) => (
                <div key={index}>
                  {page === 'ellipsis' ? (
                    <span className="flex h-9 w-9 items-center justify-center text-sm text-gray-500">
                      ...
                    </span>
                  ) : (
                    <Button
                      variant={currentPage === page ? "default" : "outline"}
                      className={`h-9 w-9 p-0 ${currentPage === page ? 'bg-red-600 hover:bg-red-700' : ''}`}
                      size="sm"
                      onClick={() => setCurrentPage(page as number)}
                    >
                      {page}
                    </Button>
                  )}
                </div>
              ))}
            </div>

            <Button
              variant="outline"
              size="sm"
              onClick={() => setCurrentPage(Math.min(totalPages, currentPage + 1))}
              disabled={currentPage === totalPages}
              className="flex items-center gap-1"
            >
              Siguiente
              <ChevronRight className="h-4 w-4" />
            </Button>
          </div>
        </div>
      )}

      {filteredWines.length === 0 && (
        <div className="text-center py-12 bg-white rounded-2xl shadow-xl border border-red-100">
          <WineIcon className="h-16 w-16 text-gray-400 mx-auto mb-4" />
          <h3 className="text-lg font-medium text-gray-900 mb-2">
            {searchTerm ? "No se encontraron vinos" : "No hay vinos en el inventario"}
          </h3>
          <p className="text-gray-500">
            {searchTerm
              ? `No hay vinos que coincidan con "${searchTerm}"`
              : "Importa un archivo Excel para comenzar"
            }
          </p>
        </div>
      )}
    </div>
  )
}

hooks.use-cart.ts:
"use client"

import { useState, useEffect, useRef } from "react"
import type { CartItem, CartItemWithWine, Wine } from "@/types"
import { parsePrice } from "@/utils/price"
import { updateURLWithData, getDataFromURL } from "@/lib/utils"

export function useCart(wines: Wine[]) {
  const [cart, setCart] = useState<CartItem[]>([])
  const isInitialized = useRef(false)

  // Cargar carrito desde URL al inicializar
  useEffect(() => {
    const savedCart = getDataFromURL('cart')
    if (savedCart && Array.isArray(savedCart)) {
      setCart(savedCart)
    }
    isInitialized.current = true
  }, [])

  // Actualizar URL cuando cambie el carrito (despu√©s del renderizado)
  useEffect(() => {
    if (isInitialized.current) {
      updateURLWithData(cart, 'cart')
    }
  }, [cart])

  const addToCart = (wineId: string) => {
    setCart((prev) => {
      const existing = prev.find((item) => item.id === wineId)
      const newCart = existing
        ? prev.map((item) => (item.id === wineId ? { ...item, quantity: item.quantity + 1 } : item))
        : [...prev, { id: wineId, quantity: 1 }]
      return newCart
    })
  }

  const removeFromCart = (wineId: string) => {
    setCart((prev) => prev.filter((item) => item.id !== wineId))
  }

  const updateQuantity = (wineId: string, quantity: number) => {
    if (quantity <= 0) {
      removeFromCart(wineId)
      return
    }
    setCart((prev) => prev.map((item) => (item.id === wineId ? { ...item, quantity } : item)))
  }

  const clearCart = () => {
    setCart([])
  }

  const cartItems: CartItemWithWine[] = cart
    .map((cartItem) => {
      const wine = wines.find((w) => w.id_vino === cartItem.id)
      return wine ? { ...wine, quantity: cartItem.quantity } : null
    })
    .filter(Boolean) as CartItemWithWine[]

  const cartTotal = cartItems.reduce((total, item) => total + parsePrice(item.precio) * item.quantity, 0)

  const cartItemCount = cart.reduce((total, item) => total + item.quantity, 0)

  return {
    cart,
    cartItems,
    cartTotal,
    cartItemCount,
    addToCart,
    removeFromCart,
    updateQuantity,
    clearCart,
  }
}

hooks.use-consumibles.ts:
import { useEffect, useState } from 'react';
import { Consumibles } from '../types';
import { getAllConsumibles, subscribeToConsumibles } from '../lib/services/consumibles-service';

export function useConsumibles() {
  const [consumibles, setConsumibles] = useState<Consumibles[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    let subscription: any;
    async function fetchConsumibles() {
      setLoading(true);
      try {
        const data = await getAllConsumibles();
        setConsumibles(data);
        setError(null);
      } catch (err: any) {
        setError(err.message || 'Error al cargar consumibles');
      } finally {
        setLoading(false);
      }
    }
    fetchConsumibles();
    subscription = subscribeToConsumibles(() => {
      fetchConsumibles();
    });
    return () => {
      if (subscription && subscription.unsubscribe) subscription.unsubscribe();
    };
  }, []);

  return { consumibles, loading, error };
}

hooks.use-filters.ts:
"use client"

import { useState, useMemo, useCallback, useEffect, useRef } from "react"
import type { Wine, FilterState } from "@/types"
import { updateURLWithData, getDataFromURL, encryptData } from "@/lib/utils"

export function useFilters(wines: Wine[]) {
  const [filters, setFilters] = useState<FilterState>({
    selectedVariedades: [],
    selectedBodegas: [],
    selectedPaises: [],
    selectedColores: [],
    priceRange: {
      min: 0,
      max: 0,
    },
  })
  const isInitialized = useRef(false)

  // Cargar filtros desde URL al inicializar
  useEffect(() => {
    const savedFilters = getDataFromURL('filters')
    if (savedFilters && typeof savedFilters === 'object') {
      // Asegurar que el rango de precio est√© presente
      const filtersWithPrice = {
        ...savedFilters,
        priceRange: savedFilters.priceRange || { min: 0, max: 0 }
      }
      setFilters(filtersWithPrice)
    }
    isInitialized.current = true
  }, [])

  // Calcular rangos de precio
  const priceRange = useMemo(() => {
    if (wines.length === 0) return { min: 0, max: 0 }

    const prices = wines.map(wine => wine.precio)
    const min = Math.min(...prices)
    const max = Math.max(...prices)

    return { min, max }
  }, [wines])

  // Actualizar URL cuando cambien los filtros (despu√©s del renderizado)
  useEffect(() => {
    if (isInitialized.current) {
      // Usar encryptData directamente para pasar el rango din√°mico
      const encrypted = encryptData(filters, priceRange)
      const url = new URL(window.location.href)
      if (encrypted) {
        url.searchParams.set('filters', encrypted)
      } else {
        url.searchParams.delete('filters')
      }
      window.history.replaceState({}, '', url.toString())
    }
  }, [filters, priceRange])

  // Inicializar rango de precio cuando se cargan los vinos
  useEffect(() => {
    if (priceRange.min > 0 && priceRange.max > 0 && filters.priceRange.min === 0 && filters.priceRange.max === 0) {
      setFilters(prev => ({
        ...prev,
        priceRange: { min: priceRange.min, max: priceRange.max }
      }))
    }
  }, [priceRange, filters.priceRange])

  const variedades = useMemo(() => {
    // Mapa para mantener el valor original pero evitar duplicados por min√∫sculas/espacios
    const map = new Map<string, string>()
    wines.flatMap((wine) => wine.variedades).forEach((v) => {
      const key = v.trim().toLowerCase()
      if (!map.has(key)) map.set(key, v.trim())
    })
    return Array.from(map.values())
  }, [wines])

  // Mapa auxiliar para normalizar y mapear variedad a su key
  const variedadKeyMap = useMemo(() => {
    const map = new Map<string, string>()
    wines.flatMap((wine) => wine.variedades).forEach((v) => {
      const key = v.trim().toLowerCase()
      if (!map.has(key)) map.set(map.size.toString(), key)
    })
    return map
  }, [wines])

  const filteredWines = useMemo(() => {
    return wines.filter((wine) => {
      const varietalMatch =
        filters.selectedVariedades.length === 0 ||
        wine.variedades.some((variedad) => filters.selectedVariedades.includes(variedad))
      const bodegaMatch = filters.selectedBodegas.length === 0 || filters.selectedBodegas.includes(wine.wine_details.bodega)
      const paisMatch = filters.selectedPaises.length === 0 || filters.selectedPaises.includes(wine.pais_importacion)
      const colorMatch = filters.selectedColores.length === 0 || filters.selectedColores.includes(wine.color_vino)
      const priceMatch = wine.precio >= filters.priceRange.min && wine.precio <= filters.priceRange.max

      return varietalMatch && bodegaMatch && paisMatch && colorMatch && priceMatch
    })
  }, [wines, filters])

  const getFilteredCountsForCategory = useCallback(
    (category: "variedades" | "bodegas" | "paises" | "colores") => {
      const tempFilters = { ...filters }

      // Remover el filtro de la categor√≠a actual para calcular contadores
      switch (category) {
        case "variedades":
          tempFilters.selectedVariedades = []
          break
        case "bodegas":
          tempFilters.selectedBodegas = []
          break
        case "paises":
          tempFilters.selectedPaises = []
          break
        case "colores":
          tempFilters.selectedColores = []
          break
      }

      // Filtrar vinos con los otros filtros aplicados
      const tempFilteredWines = wines.filter((wine) => {
        const varietalMatch =
          tempFilters.selectedVariedades.length === 0 ||
          wine.variedades.some((variedad) => tempFilters.selectedVariedades.includes(variedad))
        const bodegaMatch =
          tempFilters.selectedBodegas.length === 0 || tempFilters.selectedBodegas.includes(wine.wine_details.bodega)
        const paisMatch =
          tempFilters.selectedPaises.length === 0 || tempFilters.selectedPaises.includes(wine.pais_importacion)
        const colorMatch =
          tempFilters.selectedColores.length === 0 || tempFilters.selectedColores.includes(wine.color_vino)
        const priceMatch = wine.precio >= tempFilters.priceRange.min && wine.precio <= tempFilters.priceRange.max

        return varietalMatch && bodegaMatch && paisMatch && colorMatch && priceMatch
      })

      return tempFilteredWines
    },
    [wines, filters],
  )

  const varietalCounts = useMemo(() => {
    const counts: Record<string, number> = {}
    const relevantWines = getFilteredCountsForCategory("variedades")
    relevantWines.forEach((wine) => {
      wine.variedades.forEach((variedad) => {
        const key = variedad.trim().toLowerCase()
        counts[key] = (counts[key] || 0) + 1
      })
    })
    return counts
  }, [getFilteredCountsForCategory])

  const bodegaCounts = useMemo(() => {
    const counts: Record<string, number> = {}
    const relevantWines = getFilteredCountsForCategory("bodegas")
    relevantWines.forEach((wine) => {
      counts[wine.wine_details.bodega] = (counts[wine.wine_details.bodega] || 0) + 1
    })
    return counts
  }, [getFilteredCountsForCategory])

  const paisCounts = useMemo(() => {
    const counts: Record<string, number> = {}
    const relevantWines = getFilteredCountsForCategory("paises")
    relevantWines.forEach((wine) => {
      counts[wine.pais_importacion] = (counts[wine.pais_importacion] || 0) + 1
    })
    return counts
  }, [getFilteredCountsForCategory])

  const colorCounts = useMemo(() => {
    const counts: Record<string, number> = {}
    const relevantWines = getFilteredCountsForCategory("colores")
    relevantWines.forEach((wine) => {
      counts[wine.color_vino] = (counts[wine.color_vino] || 0) + 1
    })
    return counts
  }, [getFilteredCountsForCategory])

  const toggleVariedad = (variedad: string) => {
    setFilters((prev) => ({
      ...prev,
      selectedVariedades: prev.selectedVariedades.includes(variedad)
        ? prev.selectedVariedades.filter((v) => v !== variedad)
        : [...prev.selectedVariedades, variedad],
    }))
  }

  const toggleBodega = (bodega: string) => {
    setFilters((prev) => ({
      ...prev,
      selectedBodegas: prev.selectedBodegas.includes(bodega)
        ? prev.selectedBodegas.filter((b) => b !== bodega)
        : [...prev.selectedBodegas, bodega],
    }))
  }

  const togglePais = (pais: string) => {
    setFilters((prev) => ({
      ...prev,
      selectedPaises: prev.selectedPaises.includes(pais)
        ? prev.selectedPaises.filter((p) => p !== pais)
        : [...prev.selectedPaises, pais],
    }))
  }

  const toggleColor = (color: string) => {
    setFilters((prev) => ({
      ...prev,
      selectedColores: prev.selectedColores.includes(color)
        ? prev.selectedColores.filter((c) => c !== color)
        : [...prev.selectedColores, color],
    }))
  }

  const updatePriceRange = (min: number, max: number) => {
    setFilters((prev) => ({
      ...prev,
      priceRange: { min, max }
    }))
  }

  const clearFilters = () => {
    setFilters({
      selectedVariedades: [],
      selectedBodegas: [],
      selectedPaises: [],
      selectedColores: [],
      priceRange: { min: priceRange.min, max: priceRange.max }
    })
  }

  const hasActiveFilters =
    filters.selectedVariedades.length > 0 ||
    filters.selectedBodegas.length > 0 ||
    filters.selectedPaises.length > 0 ||
    filters.selectedColores.length > 0 ||
    filters.priceRange.min !== priceRange.min ||
    filters.priceRange.max !== priceRange.max

  const bodegas = useMemo(() => [...new Set(wines.map((wine) => wine.wine_details.bodega))], [wines])
  const paises = useMemo(() => [...new Set(wines.map((wine) => wine.pais_importacion))], [wines])
  const colores = useMemo(() => [...new Set(wines.map((wine) => wine.color_vino))], [wines])

  return {
    filters,
    filteredWines,
    variedades,
    bodegas,
    paises,
    colores,
    priceRange,
    toggleVariedad,
    toggleBodega,
    togglePais,
    toggleColor,
    updatePriceRange,
    clearFilters,
    hasActiveFilters,
    varietalCounts,
    bodegaCounts,
    paisCounts,
    colorCounts,
  }
}

hooks.use-mobile.tsx:
import * as React from "react"

const MOBILE_BREAKPOINT = 768

export function useIsMobile() {
  const [isMobile, setIsMobile] = React.useState<boolean | undefined>(undefined)

  React.useEffect(() => {
    const mql = window.matchMedia(`(max-width: ${MOBILE_BREAKPOINT - 1}px)`)
    const onChange = () => {
      setIsMobile(window.innerWidth < MOBILE_BREAKPOINT)
    }
    mql.addEventListener("change", onChange)
    setIsMobile(window.innerWidth < MOBILE_BREAKPOINT)
    return () => mql.removeEventListener("change", onChange)
  }, [])

  return !!isMobile
}

hooks.use-toast.ts:
"use client"

// Inspired by react-hot-toast library
import * as React from "react"

import type {
  ToastActionElement,
  ToastProps,
} from "@/components/ui/toast"

const TOAST_LIMIT = 1
const TOAST_REMOVE_DELAY = 1000000

type ToasterToast = ToastProps & {
  id: string
  title?: React.ReactNode
  description?: React.ReactNode
  action?: ToastActionElement
}

const actionTypes = {
  ADD_TOAST: "ADD_TOAST",
  UPDATE_TOAST: "UPDATE_TOAST",
  DISMISS_TOAST: "DISMISS_TOAST",
  REMOVE_TOAST: "REMOVE_TOAST",
} as const

let count = 0

function genId() {
  count = (count + 1) % Number.MAX_SAFE_INTEGER
  return count.toString()
}

type ActionType = typeof actionTypes

type Action =
  | {
      type: ActionType["ADD_TOAST"]
      toast: ToasterToast
    }
  | {
      type: ActionType["UPDATE_TOAST"]
      toast: Partial<ToasterToast>
    }
  | {
      type: ActionType["DISMISS_TOAST"]
      toastId?: ToasterToast["id"]
    }
  | {
      type: ActionType["REMOVE_TOAST"]
      toastId?: ToasterToast["id"]
    }

interface State {
  toasts: ToasterToast[]
}

const toastTimeouts = new Map<string, ReturnType<typeof setTimeout>>()

const addToRemoveQueue = (toastId: string) => {
  if (toastTimeouts.has(toastId)) {
    return
  }

  const timeout = setTimeout(() => {
    toastTimeouts.delete(toastId)
    dispatch({
      type: "REMOVE_TOAST",
      toastId: toastId,
    })
  }, TOAST_REMOVE_DELAY)

  toastTimeouts.set(toastId, timeout)
}

export const reducer = (state: State, action: Action): State => {
  switch (action.type) {
    case "ADD_TOAST":
      return {
        ...state,
        toasts: [action.toast, ...state.toasts].slice(0, TOAST_LIMIT),
      }

    case "UPDATE_TOAST":
      return {
        ...state,
        toasts: state.toasts.map((t) =>
          t.id === action.toast.id ? { ...t, ...action.toast } : t
        ),
      }

    case "DISMISS_TOAST": {
      const { toastId } = action

      // ! Side effects ! - This could be extracted into a dismissToast() action,
      // but I'll keep it here for simplicity
      if (toastId) {
        addToRemoveQueue(toastId)
      } else {
        state.toasts.forEach((toast) => {
          addToRemoveQueue(toast.id)
        })
      }

      return {
        ...state,
        toasts: state.toasts.map((t) =>
          t.id === toastId || toastId === undefined
            ? {
                ...t,
                open: false,
              }
            : t
        ),
      }
    }
    case "REMOVE_TOAST":
      if (action.toastId === undefined) {
        return {
          ...state,
          toasts: [],
        }
      }
      return {
        ...state,
        toasts: state.toasts.filter((t) => t.id !== action.toastId),
      }
  }
}

const listeners: Array<(state: State) => void> = []

let memoryState: State = { toasts: [] }

function dispatch(action: Action) {
  memoryState = reducer(memoryState, action)
  listeners.forEach((listener) => {
    listener(memoryState)
  })
}

type Toast = Omit<ToasterToast, "id">

function toast({ ...props }: Toast) {
  const id = genId()

  const update = (props: ToasterToast) =>
    dispatch({
      type: "UPDATE_TOAST",
      toast: { ...props, id },
    })
  const dismiss = () => dispatch({ type: "DISMISS_TOAST", toastId: id })

  dispatch({
    type: "ADD_TOAST",
    toast: {
      ...props,
      id,
      open: true,
      onOpenChange: (open) => {
        if (!open) dismiss()
      },
    },
  })

  return {
    id: id,
    dismiss,
    update,
  }
}

function useToast() {
  const [state, setState] = React.useState<State>(memoryState)

  React.useEffect(() => {
    listeners.push(setState)
    return () => {
      const index = listeners.indexOf(setState)
      if (index > -1) {
        listeners.splice(index, 1)
      }
    }
  }, [state])

  return {
    ...state,
    toast,
    dismiss: (toastId?: string) => dispatch({ type: "DISMISS_TOAST", toastId }),
  }
}

export { useToast, toast }

