"use client";

import { useWineRealtimeContext } from "@/providers/wine-realtime-provider";
import { useState, useEffect } from "react";
import { WineService } from "@/lib/services/wine-service";
import type { Wine } from "@/types";

export function useWines() {
  const {
    wines: realtimeWines,
    isLoading: realtimeLoading,
    error: realtimeError,
    refetch: refetchRealtime,
  } = useWineRealtimeContext();

  const [fallbackWines, setFallbackWines] = useState<Wine[]>([]);
  const [isLoadingFallback, setIsLoadingFallback] = useState(false);
  const [fallbackError, setFallbackError] = useState<Error | null>(null);
  const [isUsingFallback, setIsUsingFallback] = useState(false);

  const shouldUseFallback =
    (realtimeError && realtimeWines.length === 0) || isUsingFallback;

  // Cargar datos de fallback si es necesario
  useEffect(() => {
    let isMounted = true;

    if (shouldUseFallback && fallbackWines.length === 0 && !isLoadingFallback) {
      setIsLoadingFallback(true);
      setFallbackError(null);

      WineService.getAllWines()
        .then((wines) => {
          if (isMounted) {
            setFallbackWines(wines);
            setIsUsingFallback(true);
          }
        })
        .catch((error) => {
          if (!isMounted) return;
          console.error("useWines: Error loading fallback wines:", error);

          let errorMessage = "Error loading fallback wines";
          if (error instanceof Error) {
            errorMessage = error.message;
          } else if (typeof error === "string") {
            errorMessage = error;
          } else if (error && typeof error === "object") {
            errorMessage = JSON.stringify(error);
          }

          setFallbackError(new Error(errorMessage));
        })
        .finally(() => {
          if (isMounted) setIsLoadingFallback(false);
        });
    }

    return () => {
      isMounted = false;
    };
  }, [shouldUseFallback, fallbackWines, isLoadingFallback]);

  // Si vuelve realtime, restaurar el estado original
  useEffect(() => {
    if (realtimeWines.length > 0 && !realtimeError && isUsingFallback) {
      setIsUsingFallback(false);
      setFallbackWines([]);
      setFallbackError(null);
    }
  }, [realtimeWines, realtimeError, isUsingFallback]);

  const wines = shouldUseFallback ? fallbackWines : realtimeWines;
  const isLoading = shouldUseFallback ? isLoadingFallback : realtimeLoading;
  const error = shouldUseFallback ? fallbackError : realtimeError;

  const refetchWines = async () => {
    try {
      // Intentamos refetch de realtime
      await refetchRealtime();
    } catch (error) {
      console.warn("useWines: Realtime refetch failed, trying fallback:", error);

      // Si falla, intentamos el fallback
      try {
        const wines = await WineService.getAllWines();
        setFallbackWines(wines);
        setFallbackError(null);
        setIsUsingFallback(true);
      } catch (fallbackError) {
        console.error("useWines: Both realtime and fallback failed:", fallbackError);

        let errorMessage = "Both realtime and fallback failed";
        if (fallbackError instanceof Error) {
          errorMessage = fallbackError.message;
        } else if (typeof fallbackError === "string") {
          errorMessage = fallbackError;
        } else if (fallbackError && typeof fallbackError === "object") {
          errorMessage = JSON.stringify(fallbackError);
        }

        setFallbackError(new Error(errorMessage));
      }
    }
  };

  return {
    wines,
    isLoading,
    error,
    refetch: refetchWines,
    realtimeError,
    fallbackError,
    isUsingFallback,
    realtimeStatus: realtimeError
      ? "error"
      : realtimeLoading
      ? "loading"
      : "connected",

    // Funciones auxiliares
    getWineById: (id: string) => wines.find((w) => w.id_vino === id),
    getWinesByCategory: (category: string) =>
      wines.filter((w) => w.color_vino === category),
    getWinesByBodega: (bodega: string) =>
      wines.filter((w) => w.wine_details.bodega === bodega),
    getWinesByPais: (pais: string) =>
      wines.filter((w) => w.pais_importacion === pais),
    getWinesByVariedad: (variedad: string) =>
      wines.filter((w) => w.variedades.includes(variedad)),
    getWinesByPriceRange: (min: number, max: number) =>
      wines.filter((w) => w.precio >= min && w.precio <= max),
    getWinesInStock: () => wines.filter((w) => w.stock > 0),
    getWinesOutOfStock: () => wines.filter((w) => w.stock === 0),
  };
}

hooks.useRealtimeWines.ts:
import { useEffect, useCallback, useState, useRef } from "react"
import { supabase } from "@/lib/supabase"
import type { Wine } from "@/types"

export interface WineRealtimeEvent {
  type: 'INSERT' | 'UPDATE' | 'DELETE'
  wine: Wine
}

export interface UseWineRealtimeReturn {
  wines: Wine[]
  isLoading: boolean
  error: Error | null
  refetch: () => Promise<void>
}

export function useWineRealtime(
  onUpdate?: (event: WineRealtimeEvent) => void,
  onError?: (error: Error) => void
): UseWineRealtimeReturn {
  const [wines, setWines] = useState<Wine[]>([])
  const [isLoading, setIsLoading] = useState(true)
  const [error, setError] = useState<Error | null>(null)
  const channelRef = useRef<any>(null)
  const retryTimeoutRef = useRef<NodeJS.Timeout | undefined>(undefined)
  const isSubscribedRef = useRef(false)

  // Funci√≥n para cargar vinos iniciales
  const loadWines = useCallback(async () => {
    try {
      setIsLoading(true)
      setError(null)
      
      // Loading wines from Supabase...
      
      const { data, error: fetchError } = await supabase
        .from("wines")
        .select(`
          *,
          wine_details (*)
        `)
        .order("bodega", { referencedTable: 'wine_details', ascending: true })
        .order("nombre", { ascending: true })
        .order("variedades", { ascending: true })
        .order("capacidad", { ascending: false })

      if (fetchError) {
        console.error('Supabase fetch error:', fetchError)
        throw new Error(`Error fetching wines: ${fetchError.message || 'Unknown error'}`)
      }

              // Raw wines data from Supabase
      
      if (!data) {
        console.warn('No data returned from Supabase')
        setWines([])
        return
      }
      
      const normalizedWines = data.map((wine) => ({
        ...wine,
        wine_details: Array.isArray(wine.wine_details)
          ? wine.wine_details[0]
          : wine.wine_details
      }))

              // Normalized wines
      setWines(normalizedWines)
    } catch (err) {
      console.error('Error loading wines:', err)
      
      // Crear un error m√°s informativo
      let errorMessage = 'Error loading wines'
      if (err instanceof Error) {
        errorMessage = err.message
      } else if (typeof err === 'string') {
        errorMessage = err
      } else if (err && typeof err === 'object') {
        errorMessage = JSON.stringify(err)
      }
      
      const error = new Error(errorMessage)
      setError(error)
      onError?.(error)
    } finally {
      setIsLoading(false)
    }
  }, [onError])

  // Memoizar el callback para evitar re-suscripciones innecesarias
  const handleUpdate = useCallback((event: WineRealtimeEvent) => {
    try {
      setWines((prev) => {
        switch (event.type) {
          case 'INSERT':
            return [...prev, event.wine]
          case 'UPDATE':
            return prev.map((w) =>
              w.id_vino === event.wine.id_vino ? event.wine : w
            )
          case 'DELETE':
            return prev.filter((w) => w.id_vino !== event.wine.id_vino)
          default:
            return prev
        }
      })
      
      onUpdate?.(event)
    } catch (err) {
      const error = err as Error
      console.error('Error in wine realtime callback:', error)
      setError(error)
      onError?.(error)
    }
  }, [onUpdate, onError])

  // Funci√≥n para configurar el canal con reintentos
  const setupChannel = useCallback(async (retryCount = 0) => {
    try {
      // Limpiar canal anterior si existe
      if (channelRef.current) {
        // Removing previous channel
        supabase.removeChannel(channelRef.current)
        channelRef.current = null
        isSubscribedRef.current = false
      }

              // Setting up wine realtime channel
      
      channelRef.current = supabase
        .channel(`wines_changes_${Date.now()}`) // Nombre √∫nico para evitar conflictos
        .on(
          "postgres_changes",
          {
            event: "*", // insert | update | delete
            schema: "public",
            table: "wines",
          },
          (payload) => {
            try {
              // Wine realtime event
              
              // Normalizar wine_details
              const normalizeWine = (wineData: any): Wine => {
                const normalizedWine = {
                  ...wineData,
                  wine_details: Array.isArray(wineData.wine_details)
                    ? wineData.wine_details[0] || {}
                    : (wineData.wine_details || {}),
                }
                // Normalized wine in realtime
                return normalizedWine
              }

              switch (payload.eventType) {
                case "INSERT":
                  const newWine = normalizeWine(payload.new)
                  handleUpdate({
                    type: 'INSERT',
                    wine: newWine
                  })
                  break

                case "UPDATE":
                  // Para actualizaciones, necesitamos obtener los datos completos incluyendo wine_details
                  const fetchUpdatedWine = async () => {
                    try {
                      const { data: updatedWineData, error: fetchError } = await supabase
                        .from("wines")
                        .select(`
                          *,
                          wine_details (*)
                        `)
                        .eq('id_vino', payload.new.id_vino)
                        .single()

                      if (fetchError) {
                        console.error('Error fetching updated wine:', fetchError)
                        // Fallback: usar los datos del payload
                        const updatedWine = normalizeWine(payload.new)
                        handleUpdate({
                          type: 'UPDATE',
                          wine: updatedWine
                        })
                      } else {
                        const normalizedUpdatedWine = {
                          ...updatedWineData,
                          wine_details: Array.isArray(updatedWineData.wine_details)
                            ? updatedWineData.wine_details[0]
                            : updatedWineData.wine_details
                        }
                        handleUpdate({
                          type: 'UPDATE',
                          wine: normalizedUpdatedWine
                        })
                      }
                    } catch (err) {
                      console.error('Error in fetchUpdatedWine:', err)
                      // Fallback: usar los datos del payload
                      const updatedWine = normalizeWine(payload.new)
                      handleUpdate({
                        type: 'UPDATE',
                        wine: updatedWine
                      })
                    }
                  }
                  
                  fetchUpdatedWine()
                  break

                case "DELETE":
                  const deletedWine = normalizeWine(payload.old)
                  handleUpdate({
                    type: 'DELETE',
                    wine: deletedWine
                  })
                  break

                default:
                  console.warn('Unknown wine realtime event type:', (payload as any).eventType)
              }
            } catch (err) {
              // Capturar errores en el callback del canal sin propagarlos
              console.warn('Error processing wine realtime event:', err)
            }
          }
        )
        .subscribe((status) => {
          // Wine realtime subscription status
          
          if (status === 'CHANNEL_ERROR') {
            console.warn('Wine realtime channel error - attempting retry')
            const error = new Error(`Failed to subscribe to wine changes (attempt ${retryCount + 1})`)
            setError(error)
            onError?.(error)
            
            // Reintentar despu√©s de 5 segundos (m√°ximo 3 intentos)
            if (retryCount < 2) {
              retryTimeoutRef.current = setTimeout(() => {
                setupChannel(retryCount + 1)
              }, 5000)
            }
          } else if (status === 'SUBSCRIBED') {
            // Wine realtime channel subscribed successfully
            setError(null) // Limpiar errores anteriores si la suscripci√≥n es exitosa
            isSubscribedRef.current = true
          } else if (status === 'CLOSED') {
            // Wine realtime channel closed
            isSubscribedRef.current = false
          }
        })

        // Channel reference

    } catch (err) {
      const error = err as Error
      console.error('Error setting up wine realtime channel:', error)
      setError(error)
      onError?.(error)
      
      // Reintentar en caso de error de configuraci√≥n
      if (retryCount < 2) {
        retryTimeoutRef.current = setTimeout(() => {
          setupChannel(retryCount + 1)
        }, 5000)
      }
    }
  }, [handleUpdate, onError])

  useEffect(() => {
    // Cargar datos iniciales y configurar realtime
    loadWines().then(() => {
      setupChannel()
    })

    return () => {
      // Limpiar timeouts
      if (retryTimeoutRef.current) {
        clearTimeout(retryTimeoutRef.current)
      }
      
      // Limpiar canal
      if (channelRef.current) {
        // Cleaning up wine realtime channel
        supabase.removeChannel(channelRef.current)
        channelRef.current = null
        isSubscribedRef.current = false
      }
    }
  }, [loadWines, setupChannel])

  return {
    wines,
    isLoading,
    error,
    refetch: loadWines
  }
}

