"use client"
// src/providers/auth-provider.tsx
import { createContext, useContext, useEffect, useState } from "react";
import { supabase } from "@/lib/supabase";
import { User } from "@supabase/supabase-js";
import { toast } from "sonner";

interface ExtendedUser extends User {
  isAdmin?: boolean;
}

const AuthContext = createContext<any>(null);

export function AuthProvider({ children }: { children: React.ReactNode }) {
  const [user, setUser] = useState<ExtendedUser | null>(null);
  const [isLoading, setIsLoading] = useState(false);
  const [isProcessingMagicLink, setIsProcessingMagicLink] = useState(false);

  // Funci√≥n para verificar si un usuario es administrador
  const checkUserRole = (user: User): ExtendedUser => {
    const extendedUser = user as ExtendedUser;
    // Verificar en user_metadata si es admin
    extendedUser.isAdmin = user.user_metadata?.role === 'admin' || 
                          user.user_metadata?.isAdmin === true ||
                          user.email === 'acevedojuanesteban.colombia@gmail.com'; // Email espec√≠fico como admin

    return extendedUser;
  };

  // Funci√≥n para procesar el magic link desde el hash de la URL
  const processMagicLink = async () => {
    if (typeof window === 'undefined') return;

    const hash = window.location.hash;
    if (!hash) return;

    // Extraer par√°metros del hash
    const params = new URLSearchParams(hash.substring(1));
    const accessToken = params.get('access_token');
    const refreshToken = params.get('refresh_token');
    const expiresAt = params.get('expires_at');
    const tokenType = params.get('token_type');

    if (accessToken && refreshToken) {
      setIsProcessingMagicLink(true);
      try {
        // Establecer la sesi√≥n con los tokens
        const { data, error } = await supabase.auth.setSession({
          access_token: accessToken,
          refresh_token: refreshToken,
        });

        if (error) {
          console.error('Error al procesar magic link:', error);
          toast.error('Error al procesar el enlace de acceso');
        } else if (data.session) {
          const extendedUser = checkUserRole(data.session.user);
          setUser(extendedUser);
          toast.success('¬°Sesi√≥n iniciada correctamente!');

          // Limpiar el hash de la URL
          window.history.replaceState(null, '', window.location.pathname + window.location.search);
        }
      } catch (error) {
        console.error('Error al procesar magic link:', error);
        toast.error('Error al procesar el enlace de acceso');
      } finally {
        setIsProcessingMagicLink(false);
      }
    }
  };

  useEffect(() => {
    // Procesar magic link al cargar la p√°gina
    processMagicLink();

    // Obtener sesi√≥n actual
    supabase.auth.getSession().then(({ data }) => {
      if (data.session?.user) {
        const extendedUser = checkUserRole(data.session.user);
        setUser(extendedUser);
      }
    });

    // Escuchar cambios en el estado de autenticaci√≥n
    const { data: listener } = supabase.auth.onAuthStateChange((_event, session) => {
      if (session?.user) {
        const extendedUser = checkUserRole(session.user);
        setUser(extendedUser);
      } else {
        setUser(null);
      }
    });

    return () => {
      listener?.subscription.unsubscribe();
    };
  }, []);

  const login = async (email: string) => {
    setIsLoading(true);
    try {
      const { error } = await supabase.auth.signInWithOtp({ 
        email,
        options: {
          emailRedirectTo: `${window.location.origin}${window.location.pathname}`
        }
      });

      if (error) {
        throw error;
      } else {
        toast.success('Enlace de acceso enviado a tu email');
      }
    } catch (error: any) {
      console.error('Error al enviar magic link:', error);
      toast.error(error.message || 'Error al enviar el enlace de acceso');
      throw error;
    } finally {
      setIsLoading(false);
    }
  };

  const logout = async () => {
    try {
      await supabase.auth.signOut();
      setUser(null);
      toast.success('Sesi√≥n cerrada correctamente');
    } catch (error) {
      console.error('Error al cerrar sesi√≥n:', error);
      toast.error('Error al cerrar sesi√≥n');
    }
  };

  // Funci√≥n para actualizar el rol del usuario (solo para admins)
  const updateUserRole = async (userId: string, isAdmin: boolean) => {
    try {
      const { error } = await supabase.auth.admin.updateUserById(userId, {
        user_metadata: { 
          role: isAdmin ? 'admin' : 'user',
          isAdmin: isAdmin 
        }
      });

      if (error) throw error;

      // Actualizar el usuario local si es el usuario actual
      if (user?.id === userId) {
        const updatedUser = { ...user, isAdmin };
        setUser(updatedUser);
      }

      toast.success(`Usuario ${isAdmin ? 'promovido a administrador' : 'degradado a usuario'}`);
    } catch (error: any) {
      console.error('Error al actualizar rol:', error);
      toast.error('Error al actualizar el rol del usuario');
      throw error;
    }
  };

  return (
    <AuthContext.Provider value={{ 
      user, 
      isLoggedIn: !!user,
      login, 
      logout, 
      isLoading, 
      isProcessingMagicLink,
      updateUserRole
    }}>
      {children}
    </AuthContext.Provider>
  );
}

export function useAuth() {
  return useContext(AuthContext);
}

providers.cart-provider.tsx:
"use client"

import React, { createContext, useContext, useState, ReactNode } from "react"

interface CartContextProps {
  isCartOpen: boolean
  openCart: () => void
  closeCart: () => void
  toggleCart: () => void
}

const CartContext = createContext<CartContextProps | undefined>(undefined)

interface CartProviderProps {
  children: ReactNode
}

export const CartProvider = ({ children }: CartProviderProps) => {
  const [isCartOpen, setIsCartOpen] = useState(false)

  const openCart = () => setIsCartOpen(true)
  const closeCart = () => setIsCartOpen(false)
  const toggleCart = () => setIsCartOpen(!isCartOpen)

  return (
    <CartContext.Provider value={{ isCartOpen, openCart, closeCart, toggleCart }}>
      {children}
    </CartContext.Provider>
  )
}

export function useCartModal() {
  const context = useContext(CartContext)
  if (!context) {
    throw new Error("useCartModal debe usarse dentro de un CartProvider")
  }
  return context
}

providers.order-status-provider.tsx:
"use client"

import React, { createContext, useContext, useState, useEffect, ReactNode } from "react"

type OrderStatusType = "pendiente" | "preparandose" | "listo para enviarse" | "enviandose" | "listo"

interface OrderStatusContextProps {
  userId: string | null
  setUserId: (id: string) => void
  orderStatus: OrderStatusType
  setOrderStatus: (status: OrderStatusType) => void
}

const OrderStatusContext = createContext<OrderStatusContextProps | undefined>(undefined)

interface OrderStatusProviderProps {
  children: ReactNode
}

export const OrderStatusProvider = ({ children }: OrderStatusProviderProps) => {
  const [userId, setUserIdState] = useState<string | null>(null)
  const [orderStatus, setOrderStatusState] = useState<OrderStatusType>("pendiente")

  // Funci√≥n para obtener las claves de localStorage basadas en el userId
  const getStorageKeys = (id: string) => ({
    userIdKey: id,
    orderStatusKey: `wine-store-order-status-${id}`
  })

  // Cargar desde localStorage al iniciar
  useEffect(() => {
    // Buscar si hay alg√∫n userId guardado
    const keys = Object.keys(localStorage)
    const userIdKey = keys.find(key => key.startsWith('wine-store-user-id-'))
    
    if (userIdKey) {
      const storedUserId = localStorage.getItem(userIdKey)
      if (storedUserId) {
        setUserIdState(storedUserId)
        const { orderStatusKey } = getStorageKeys(storedUserId)
        const storedOrderStatus = localStorage.getItem(orderStatusKey) as OrderStatusType | null
        if (storedOrderStatus) setOrderStatusState(storedOrderStatus)
      }
    }
  }, [])

  // Guardar en localStorage cuando cambie el userId
  useEffect(() => {
    if (userId) {
      const { userIdKey } = getStorageKeys(userId)
      localStorage.setItem(userIdKey, userId)
    }
  }, [userId])

  // Guardar en localStorage cuando cambie el orderStatus
  useEffect(() => {
    if (userId && orderStatus) {
      const { orderStatusKey } = getStorageKeys(userId)
      localStorage.setItem(orderStatusKey, orderStatus)
    }
  }, [userId, orderStatus])

  const setUserId = (id: string) => setUserIdState(id)
  const setOrderStatus = (status: OrderStatusType) => setOrderStatusState(status)

  return (
    <OrderStatusContext.Provider value={{ userId, setUserId, orderStatus, setOrderStatus }}>
      {children}
    </OrderStatusContext.Provider>
  )
}

export function useOrderStatus() {
  const context = useContext(OrderStatusContext)
  if (!context) {
    throw new Error("useOrderStatus debe usarse dentro de un OrderStatusProvider")
  }
  return context
}

